from Crypto.Util.number import *

p, q = [getPrime(512) for _ in range(2)]
m = bytes_to_long(b'Cry')
e = 0x10001
d = inverse(e, (p - 1)*(q - 1))
c = pow(m, e, p*q)

print(f"p = {p}")
print(f"q = {q}")
print(f"e = {e}")
print(f"c = {c}")

